ruleToc.chapterList:
@css:
#allchapter>.title, #allchapter>.details

@js:
list=[]
$=result
for(i in $){
if(String($[i]).match(/class="title/)){
		list.push({
				text: "📖 [ "+$[i].text()+" ] 📖",
				volume: true
			})
	}
else{
		$[i].select("a").forEach(a=>{
				list.push({
						text: a.text(),
						href: a.attr("href")
					})
			})
	}
}
list

ruleContent.replaceRegex:
@js:
var f=String(src).match(/url\('([^']+)'\) format\('truetype'\)/);
if (f) {
    var f1 = java.queryTTF("http:"+f[1])
    var f2 = java.queryBase64TTF("")
    java.replaceFont(result, f1, f2)
}else{
    result
}

