@js:
base=baseUrl.replace(/_.+?$/,'_')
code=["192aac","128mp3","flac"]


function $(url){
		return url.match(/guid/)?true:false
	}

function getUrl(){
		ajaxUrl(code[i])
		return $(url)?url:(i++,(i<3?getUrl():java.toast('没有链接')))
	}

function ajaxUrl(code){
	try{
		ajax = java.ajax(base+code)
		return url=JSON.parse(ajax).data.url
	}
	catch (e){
		return url=''
	}
}

i=0;url=result
$(url)?url:getUrl()
