@js:catlist=[]
J=org.jsoup.Jsoup.parse(java.ajax(source.getKey()))
tags=J.select('div:has(div:has(div:has(h2)))>div')
for(index=2;index<tags.size();index++){
	  clist=tags.get(index).select('ul>li')
	  typename=tags.get(index).select('div>h2').text()
	  catlist.push({
	      "title": '◆◇'+String(typename)+'◇◆',
	      "style": {
          "layout_flexGrow": 1,
	         "layout_flexBasisPercent": 1
       }
	  })
	  for(next=1;next<clist.size();next++){
	  	  info=clist.get(next).select('a').get(0)
	  	  span=info.select('span')
	  	  title=String(span.get(span.size()-1).text())
	     catlist.push({
	  	      "title": title.replace(/\s*-.*$/,''),
	  	      "url": String(info.attr('href')),
	  	      "style": {
             "layout_flexGrow": 1,
	            "layout_flexBasisPercent": 0.4
          }
	     })
	  }
}
JSON.stringify(catlist)

